import React, { useState } from 'react'
import s from './SearchBar.module.css'
import { useDispatch } from "react-redux";
import * as actions from "../../redux/actions";


export default function Search() {
  const [name, setName] = useState("")
  const [error, setError] = useState(null);
  const dispatch = useDispatch();

  const handleError = (error) => {
    if (error.response && error.response.status === 404) {
      setError('Pokemon not fount');
      dispatch(actions.setSearchError('Pokemon not fount'));
    }
  };

  function handleInputChange(e) {
    e.preventDefault();
    setName(e.target.value)
    setError(null)
    console.log('search', e.target.value)
  }

  function handleSearch(e) {
    e.preventDefault();
    if(!name){
      setError('Pokemon not found')
      dispatch(actions.setSearchError('Pokemon not found'));
    }
    dispatch(actions.searchPokemon(name));
    //console.log('name handle search', name)
    setName(''); //vacio el input
  }


  return (
    <form className={s.searchContainer}>
      <input 
        className={s.searchBar} 
        type='text' 
        placeholder= "Search by name"
        onChange={(e) => handleInputChange(e)} 
        value={name} 
      />
      {error && <h2>{error}</h2>}
      <button className={s.btnSearch} onClick={(e) => handleSearch(e)}>GO</button>
    </form>
     
  )
}
