import React from 'react';
import s from './Filters.module.css';

import { filterByType } from '../../redux/actions';

export default function Filters() {
  const handleMenuOne = () => {
    console.log('clicked one');
  };

  const handleMenuTwo = () => {
    console.log('clicked two');
  }

  const [selectValue, setSelectValue] = React.useState("");
  const onChange = (event) => {
    const value = event.target.value;
    setSelectValue(value);
  };


  return (
    <div className={s.filterSection}>
      <div className={s.filters}>
        <h2 className={s.filterTitle}>Filters</h2>
        <div className={s.filterBy}>
          <h3 className={s.filterSubitle}>Filter by type</h3>
          <select onChange={onChange} className={s.select}>
            <option defaultValue disabled>
              Select a Pokemon type
            </option>
            <option onClick={filterByType()} value="normal">normal</option>
            <option value="fighting">fighting</option>
            <option value="flying">flying</option>
            <option value="poison">poison</option>
            <option value="ground">ground</option>
            <option value="rock">rock</option>
            <option value="bug">bug</option>
            <option value="ghost">ghost</option>
            <option value="steel">steel</option>
            <option value="fire">fire</option>
            <option value="water">water</option>
            <option value="grass">grass</option>
            <option value="electric">electric</option>
            <option value="psychic">psychic</option>
            <option value="ice">ice</option>
            <option value="dragon">dragon</option>
            <option value="dark">dark</option>
            <option value="fairy">fairy</option>
            <option value="unknow">unknow</option>
            <option value="shadow">shadow</option>
          </select>
          {selectValue && <h2 className="mt-3">{selectValue}</h2>}
        </div>
      </div>
        <div className={s.filterBy}>
          <h3>Created in</h3>
          
        </div>    
      </div>
    
  )
}

