import React, { useState } from 'react'
import s from './Filters.module.css';

import { useDispatch, useSelector } from "react-redux";
import * as actions from "../../redux/actions";

export default function Sorts() {
  const [selectValue, setSelectValue] = React.useState("");
  const [order, setOrder] = useState("asc");

  const dispatch = useDispatch();
  const pokemons = useSelector((state) => state.pokemons);
  
  function handleSortByAlpha(e){
    e.prevent.Default();
    dispatch(actions.sortByAlphabet(e.target.value));
  }

  // function sortByAlphaDesc(e){
  //   const value = e.target.value;
  //   setSelectValue(value);
  //   dispatch(actions.sortByAlphabetDes(value));
  // }


  React.useEffect(() => {
    if (!pokemons[0]) {
      dispatch(actions.getPokemons());
      dispatch(actions.getTypes());
    }
  }, [dispatch, pokemons]);
  
  
  return (
    <div className={s.filters}>
      <div className={s.filterBy}> 
        <h3>Sort by Alphabet</h3>
        <select 
          id='pokemons-sorted-alpha'
          value={order}
          onChange={(e) => handleSortByAlpha(e)}
        >
        <option value="default" disabled hidden>
              Sort by Alphabet
            </option>
            <option value="asc">From A to Z</option>
            <option value="desc">From Z to A</option>
        </select>
      </div>
    </div>
  )
}

