import React from 'react'
import s from './Create.module.css';

import { useDispatch } from "react-redux";
import * as actions from '../../redux/actions'

export default function Create() {
  const dispatch = useDispatch();
  const [input, setInput] = React.useState({
    name: "",
    attack: "",
    defense: "",
    speed: "",
    hp: "",
    height: "",
    weight: "",
    image: "",
    created: ""
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(actions.createPokemon(input))
  }

  const handleChange = (e) => {
    setInput({
      ...input,
      [e.target.name]: e.target.value
    })
  }; 


  return (
    <div className={s.formSection}>
      <h2>Create your own Pokemon</h2>
      <div className={s.form}>
        <form onSubmit={handleSubmit}>
        <div className={s.inputs}>
          <div>
          <label htmlFor="name">Name: </label>
          <input
            type='text'
            id="name"
            name="name"
            value={input.name}
            onChange={handleChange}
          />
          </div>
          <div>
          <label htmlFor="attack">Attack: </label>
          <input
            type='text'
            id="attack"
            name="attack"
            value={input.attack}
            onChange={handleChange}
          />
          </div>
          <label htmlFor="defense">Defense: </label>
          <input
            type='text'
            id="defense"
            name="defense"
            value={input.defense}
            onChange={handleChange}
          />

          <label htmlFor="speed"><input
            type='text'
            id="speed"
            name="speed"
            value={input.speed}
            onChange={handleChange}
          />Speed: </label>

          <label htmlFor="hp"><input
            type='text'
            id="hp"
            name="hp"
            value={input.hp}
            onChange={handleChange}
          />Hp: </label>

          <label htmlFor="height"><input
            type='text'
            id="height"
            name="height"
            value={input.height}
            onChange={handleChange}
          />Height: </label>

          <label htmlFor="weight"><input
            type='text'
            id="weight"
            name="weight"
            value={input.weight}
            onChange={handleChange}
          />Weight: </label>

          <label htmlFor="created"><input
            type='text'
            id="creates"
            name="created"
            value={input.created}
            onChange={handleChange}
          />Created: </label>
        </div>
        <div className={s.formBtn}>
          <button type="submit">Create Pokemon</button>
        </div>
      </form>
    </div>
    </div>
  )
}
