import {
    GET_POKEMONS,
    GET_POKEMON_DETAIL,
    CREATE_POKEMON,
    DELETE_POKEMON,
    GET_TYPES,
    SORT_BY_ALPHABET,
    SORT_BY_ATTACK,
    FILTER_BY_CREATED,
    FILTER_BY_TYPE,
    SEARCH_POKEMON
} from './actions'

const initialState = {
    pokemons: [],
    pokemonDetail: {},
    types: [],
    filterByType: null
}

const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case GET_POKEMONS:
            return{
                ...state,
                pokemons: action.payload,
            }
        case GET_POKEMON_DETAIL:
            return{
                ...state,
                pokemonDetail: action.payload
            }
        case CREATE_POKEMON:
            return{
                ...state,
                pokemons: state.pokemons.concat(action.payload)
            }
        case DELETE_POKEMON:
            return{
                ...state,
                pokemons: state.pokemons.filter((pokemon) => pokemon.id !== action.payload) 
            }
        case GET_TYPES:
            return{
                ...state,
                types: action.payload
            }
        case SORT_BY_ALPHABET: //si recibe value asc ordena de una forma
            let sortAlpha  = action.payload === 'asc' ?
            state.pokemons.sort(function(a, b){
                if(a.name.toLowerCase() > b.name.toLowerCase()) {
                    return 1; //paso todo a minusculas para comparar sin problemas
                }
                if(b.name.toLowerCase() > a.name.toLowerCase()) {
                    return -1;
                }
                return 0;
            }) : //si el value es desc el orden es inverso
            state.pokemons.sort(function(a, b){
                if(a.name.toLowerCase() > b.name.toLowerCase()) {
                    return -1;
                }
                if(b.name.toLowerCase() > a.name.toLowerCase()) {
                    return 1;
                }
                return 0;
            })
            return {
                ...state, //la array de pokemons va a tomar el orden que surge de sortAlpha
            pokemons: sortAlpha,
        }
        case SORT_BY_ATTACK:
            let sortAttack  = action.payload.sort((a, b) => (a.attack < b.attack ? 1 : a.attack > b.attack ? -1 : 0)); 
            return{
                ...state,
                pokemons: sortAttack,
        }
        case FILTER_BY_TYPE:
            console.log('filterByType', action.payload)
        return{
            ...state,
            filterByType: action.payload
        }
        case FILTER_BY_CREATED:
        return{
            ...state,
            pokemons: action.payload
        }
        case SEARCH_POKEMON:
            return {
                ...state,
                pokemons: action.payload
            }
        default: return {...state}
    }
};

export default rootReducer;
