import React, { useState, useEffect } from 'react'
import { useDispatch, useSelector } from "react-redux";
import * as actions from "../../redux/actions";
import s from './SearchBar.module.css'


export default function Search() {
  const [name, setName] = useState("")
  const error = useSelector(state => state.error);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(actions.searchPokemon(name));
  }, [name, dispatch])
  
  function handleInputChange(e) {
    e.preventDefault();
    setName(e.target.value)
    console.log('search', e.target.value)
  }

  function handleSearch(e) {
    e.preventDefault();
    if(!name){
      setError('Pokemon not found')
      dispatch(actions.setSearchError('Pokemon not found'));
    }
    dispatch(actions.searchPokemon(name));
    //console.log('name handle search', name)
    setName(''); //vacio el input
  }


  return (
    <form className={s.searchContainer}>
      <input 
        className={s.searchBar} 
        type='text' 
        placeholder= "Search by name"
        onChange={(e) => handleInputChange(e)} 
        value={name} 
      />
      {error && <h2>{error}</h2>}
      <button className={s.btnSearch} onClick={(e) => handleSearch(e)}>GO</button>
    </form>
     
  )
}
