import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { updatePokemon } from "../../redux/actions";
import axios from "axios";

//FUNCION VALIDADORA
function validate(input){  //va a recibir el estado input con los cambios detectados por los handlers
  let errors = {};  //objeto que guarda todos los errores y le agrego props con los nombres iguales a los del input
  if(!update.name){                               
      errors.name = 'a name is required';//al obj errors le agrego una prop name q tiene un mensaje como valor
  }else if(!/^[A-z]+$/.test(update.name)){  //regex solo acepta letras
      errors.name = 'only letters allowed'
  }else if(!update.img){
      errors.img = 'insert an url';
  }else if(!/^https?:\/\/(?:www\.)?[-a-zA-Z0-9@:%._\+~#=]{1,256}\.[a-zA-Z0-9()]{1,6}\b(?:[-a-zA-Z0-9()@:%_\+.~#?&\/=]*)$/.test(update.img)){ 
      errors.img = 'only URL directions allowed'
  }else if(update.hp < 1 || update.hp > 200){
      errors.hp = 'must be a value between 1 and 200'
  }else if(!/^[0-9]+$/.test(update.hp)){ 
      errors.hp = 'only numbers allowed'
  }else if(update.attack < 1 || update.attack > 200){
      errors.attack = 'must be a value between 1 and 200'
  }else if(!/^[0-9]+$/.test(update.attack)){ 
      errors.attack = 'only numbers allowed'
  }else if(update.defense < 1 || update.defense > 200){
      errors.defense = 'must be a value between 1 and 200'
  }else if(!/^[0-9]+$/.test(update.defense)){ 
      errors.defense = 'only numbers allowed'
  }else if(update.speed < 1 || update.speed > 200){
      errors.speed = 'must be a value between 1 and 200'
  }else if(!/^[0-9]+$/.test(update.speed)){ 
      errors.speed = 'only numbers allowed'
  }else if(update.height < 1 || update.height > 200){
      errors.height = 'must be a value between 1 and 200'
  }else if(!/^[0-9]+$/.test(update.height)){ 
      errors.height = 'only numbers allowed'
  }else if(update.weight < 1 || update.weight > 200){
      errors.weight = 'must be a value between 1 and 200'
  }else if(!/^[0-9]+$/.test(update.weight)){ 
      errors.weight = 'only numbers allowed'
  }else if(update.types.length < 1){
      errors.types = 'select at least 1 type'
  }
      return errors;      //se retorna el obj errors con la prop y el string correspondiente. let errors = {name: 'se requiere un nombre'}
}


export default function Update(props) {
  const pokemonDetail = useSelector((state) => state.pokemonDetail);
  const dispatch = useDispatch();
  //estado local para almacenar los datos del form
  //pruebo con props.pokemonDetail pero puede que sea sin props,
  //o algo por estilo que me traiga las propiedades del objeto

  const [name, setName] = useState(pokemonDetail.name);
  const [img, setImg] = useState(pokemonDetail.img);
  const [hp, setHp] = useState(pokemonDetail.hp);
  const [attack, setAttack] = useState(pokemonDetail.attack);
  const [defense, setDefense] = useState(pokemonDetail.defense);
  const [speed, setSpeed] = useState(pokemonDetail.speed);
  const [height, setHeight] = useState(pokemonDetail.height);
  const [weight, setWeight] = useState(pokemonDetail.weight);
  const [types, setTypes] = useState(pokemonDetail.types);

  const handleSubmit = (e) => {
    e.preventDefault();
    //creo el obj de actualizacion a partir del estado local
    const updates = {
      name,
      img,
      hp,
      attack,
      defense,
      speed,
      height,
      weight,
      types,
    };
    // Llamo a la accion
    // Envía una solicitud PATCH al servidor para actualizar el elemento
    axios
      .patch(`http://localhost:3001/pokemons/${pokemonDetail.id}`, { updates })
      .then(() => {
        dispatch(updatePokemon(pokemonDetail.id, updates));
      });
  };

  function handleSelect(e) {
    //recibe el tipo que se seleccionó en el selector
    if (!types.includes(e.target.value)) {
      //evita que se repitan los tipos
      setTypes({
        types: [...types, e.target.value], //al array de la prop types le añade el nuevo tipo que se seleccionó
      });
    }
    setErrors(validate({
      ...update,
            types: [...input.types, e.target.value]
    }))
  }

  function handleDelete(el) {
    //recibe un evento, que es el click en la X de un tipo
    setTypes({
      ...input,
      types: input.types.filter((t) => t !== el), //se filtra el array de la prop type, dejando pasar solo aquellos tipos que no coinciden con el clickeado
    });
  }

  return (
    <div>
      <h1>Edit your Pokemon</h1>
      <form onSubmit={handleSubmit}>
      <label htmlFor="name">Name: </label>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
       
        <input type="text" value={hp} onChange={(e) => setHp(e.target.value)} />
        <input
          type="number"
          value={attack}
          onChange={(e) => setAttack(e.target.value)}
        />
        <input
          type="number"
          value={defense}
          onChange={(e) => setDefense(e.target.value)}
        />
        <input
          type="number"
          value={speed}
          onChange={(e) => setSpeed(e.target.value)}
        />
        <input
          type="number"
          value={height}
          onChange={(e) => setHeight(e.target.value)}
        />
        <input
          type="number"
          value={weight}
          onChange={(e) => setWeight(e.target.value)}
        />
        <input
          type="text"
          value={img}
          onChange={(e) => setImg(e.target.value)}
        />
        <div className={s.typeForm}>
              {input.types.length < 2 ? ( //si ya eligio 2, no se muestran las opciones
                <select value="default" onChange={(e) => handleSelect(e)}>
                  
                  {/*Cuando se selecciona una opcion se ejecuta handleSelect con esa selección*/}
                  <option value="default" disabled hidden>
                    Pokemon type
                  </option>
                  <option value="bug">bug</option>
                  <option value="dark">dark</option>
                  <option value="dragon">dragon</option>
                  <option value="electric">electric</option>
                  <option value="fairy">fairy</option>
                  <option value="fighting">fighting</option>
                  <option value="flying">flying</option>
                  <option value="fire">fire</option>
                  <option value="ghost">ghost</option>
                  <option value="grass">grass</option>
                  <option value="ground">ground</option>
                  <option value="ice">ice</option>
                  <option value="poison">poison</option>
                  <option value="psychic">psychic</option>
                  <option value="rock">rock</option>
                  <option value="shadow">shadow</option>
                  <option value="steel">steel</option>
                  <option value="unknow">unknow</option>
                  <option value="water">water</option>
                </select>
              ) : (
                <p className={s.error}>cannot have more than 2 types</p>
              )}
            </div>
            {errors.types && <p className={s.error}>{errors.types}</p>}
                {/*Se va a mostrar cada type seleccionado*/}
            <div className={s.input}>
              {input.types.map((el) => ( //Recorro el array de la prop type del input
                  <div className={s.typeContent}>
                    
                    {/*renderizo el type que ya fue seleccionado con un boton X*/}
                    <p>{el}</p>
                    <button
                      className={s.deleteType}
                      type="button"
                      onClick={() => handleDelete(el)}
                    >
                      x
                    </button>
                    {/*cuando clickeo en X se ejecuta handleDelete*/}
                  </div>
                )
              )}
            </div>
         
        <button type="submit">Update</button>
      </form>
    </div>
  );
}
