import React from 'react'
import s from './Create.module.css';

import { useDispatch } from "react-redux";
import * as actions from '../../redux/actions'

export default function Create() {
  const dispatch = useDispatch();
  const [input, setInput] = React.useState({
    name: "",
    attack: "",
    defense: "",
    speed: "",
    hp: "",
    height: "",
    weight: "",
    image: "",
    created: ""
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(actions.createPokemon(input))
  }

  const handleChange = (e) => {
    setInput({
      ...input,
      [e.target.name]: e.target.value
    })
  }; 


  return (
    <div >
      <h2>Create your own Pokemon</h2>
      <form onSubmit={handleSubmit}>

        <label htmlFor="name"><input
          type='text'
          id="name"
          name="name"
          value={input.name}
          onChange={handleChange}
        />Name: </label>

        <label htmlFor="attack"><input
          type='text'
          id="attack"
          name="attack"
          value={input.attack}
          onChange={handleChange}
        />Attack: </label>

        <label htmlFor=""><input
          type='text'
          id=""
          name="name"
          value={input.name}
          onChange={handleChange}
        />Name: </label>

        <label htmlFor="name"><input
          type='text'
          id="name"
          name="name"
          value={input.name}
          onChange={handleChange}
        />Name: </label>

        <label htmlFor="name"><input
          type='text'
          id="name"
          name="name"
          value={input.name}
          onChange={handleChange}
        />Name: </label>

        <label htmlFor="name"><input
          type='text'
          id="name"
          name="name"
          value={input.name}
          onChange={handleChange}
        />Name: </label>

        <label htmlFor="name"><input
          type='text'
          id="name"
          name="name"
          value={input.name}
          onChange={handleChange}
        />Name: </label>
        <button type="submit">Create Pokemon</button>
      </form>
    </div>
  )
}
