import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { updatePokemon } from "../../redux/actions";
import axios from 'axios';

export default function Update(props) {
    const pokemonDetail = useSelector((state) => state.pokemonDetail);
    const dispatch = useDispatch();
    //estado local para almacenar los datos del form
  //pruebo con props.pokemonDetail pero puede que sea sin props,
  //o algo por estilo que me traiga las propiedades del objeto
  
  const [name, setName] = useState(props.pokemonDetail.name);
  const [img, setImg] = useState(props.pokemonDetail.img);
  const [hp, setHp] = useState(props.pokemonDetail.hp);
  const [attack, setAttack] = useState(props.pokemonDetail.attack);
  const [defense, setDefense] = useState(props.pokemonDetail.defense);
  const [speed, setSpeed] = useState(props.pokemonDetail.speed);
  const [height, setHeight] = useState(props.pokemonDetail.height);
  const [weight, setWeight] = useState(props.pokemonDetail.weight);
  const [types, setTypes] = useState(props.pokemonDetail.types);

  const handleSubmit = (e) => {
    e.preventDefault();
    //creo el obj de actualizacion a partir del estado local
    const updates = {
      name,
      img,
      hp,
      attack,
      defense,
      speed,
      height,
      weight,
      types,
    };
    // Llamo a la accion
     // Envía una solicitud PATCH al servidor para actualizar el elemento
     axios.patch(`http://localhost:3001/pokemons/${pokemonDetail.id}`, { updates })
     .then(() => {
    dispatch(updatePokemon(pokemonDetail.id, updates));
  });
}


  return (
    <div>
      <h1>Edit your Pokemon</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          type="text"
          value={img}
          onChange={(e) => setImg(e.target.value)}
        />
        <input type="text" value={hp} onChange={(e) => setHp(e.target.value)} />
        <input
          type="text"
          value={attack}
          onChange={(e) => setAttack(e.target.value)}
        />
        <input
          type="text"
          value={defense}
          onChange={(e) => setDefense(e.target.value)}
        />
        <input
          type="text"
          value={speed}
          onChange={(e) => setSpeed(e.target.value)}
        />
        <input
          type="text"
          value={height}
          onChange={(e) => setHeight(e.target.value)}
        />
        <input
          type="text"
          value={weight}
          onChange={(e) => setWeight(e.target.value)}
        />
        <input
          type="text"
          value={types}
          onChange={(e) => setTypes(e.target.value)}
        />
        <button type='submit'>Update</button>
      </form>
    </div>
  );
}
