import React from 'react'
import s from './Create.module.css';

import { useDispatch } from "react-redux";
import * as actions from '../../redux/actions'

export default function Create() {
  const dispatch = useDispatch();
  const [input, setInput] = React.useState({
    name: "",
    attack: "",
    defense: "",
    speed: "",
    hp: "",
    height: "",
    weight: "",
    image: "",
    created: ""
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(actions.createPokemon(input))
  }

  const handleChange = (e) => {
    setInput({
      ...input,
      [e.target.name]: e.target.value
    })
  }; 


  return (
    <div >
      <h2>Create your own Pokemon</h2>
      <div className={s.inputs}>
        <form onSubmit={handleSubmit}>

          <label htmlFor="name"><input
            type='text'
            id="name"
            name="name"
            value={input.name}
            onChange={handleChange}
          />Name: </label>

          <label htmlFor="attack"><input
            type='text'
            id="attack"
            name="attack"
            value={input.attack}
            onChange={handleChange}
          />Attack: </label>

          <label htmlFor="defense"><input
            type='text'
            id="defense"
            name="defense"
            value={input.defense}
            onChange={handleChange}
          />Defense: </label>

          <label htmlFor="speed"><input
            type='text'
            id="speed"
            name="speed"
            value={input.speed}
            onChange={handleChange}
          />Speed: </label>

          <label htmlFor="hp"><input
            type='text'
            id="hp"
            name="hp"
            value={input.hp}
            onChange={handleChange}
          />Hp: </label>

          <label htmlFor="height"><input
            type='text'
            id="height"
            name="height"
            value={input.height}
            onChange={handleChange}
          />Height: </label>

          <label htmlFor="weight"><input
            type='text'
            id="weight"
            name="weight"
            value={input.weight}
            onChange={handleChange}
          />Weight: </label>

          <label htmlFor="created"><input
            type='text'
            id="creates"
            name="created"
            value={input.created}
            onChange={handleChange}
          />Created: </label>
        <button type="submit">Create Pokemon</button>
      </form>
    </div>

    </div>
  )
}
