import React from 'react';
import s from './Filters.module.css';

import { filterByType } from '../../redux/actions';
import { useDispatch, useSelector } from 'react-redux';
import * as actions from '../../redux/actions'

export default function Filters() {
  const [selectValue, setSelectValue] = React.useState("");
  
  const handlechange = (event) => {
    const value = event.target.value;
    setSelectValue(value);
  };

  const dispatch = useDispatch();
  const pokemons = useSelector((state)=> state.pokemons);
  //const types = useSelector((state) => state.types);

  React.useEffect(() => {
    if(!pokemons[0]){
      dispatch(actions.getPokemons());
      dispatch(actions.getTypes());
    }
  }, [dispatch, pokemons]);

  // function handleClick(e){           
  //   e.preventDefault();             
  //   dispatch(actions.getPokemons())         
  // };

  function handleFilterType(e) {
    const value = e.target.value;
    setSelectValue(value);
    dispatch(filterByType(e.target.value))
  }


  return (
    <div className={s.filterSection}>
      <div className={s.filters}>
        <h2 className={s.filterTitle}>Filters</h2>
        <div className={s.filterBy}>
          <h3 className={s.filterSubitle}>Filter by type</h3>
          
          <select className={s.select} value='default' onChange={handlechange}>
            <option value='default' disabled hidden>Pokemon type</option>
            <option handlechange={()=> filterByType(setSelectValue)} value="bug">bug</option>
            <option handlechange={()=> filterByType(setSelectValue)} value="dark">dark</option>
            <option handlechange={()=> filterByType(setSelectValue)} value="dragon">dragon</option>
            <option handlechange={()=> filterByType(setSelectValue)} value="electric">electric</option>
            <option value="fairy">fairy</option>
            <option value="fighting">fighting</option>
            <option value="flying">flying</option>
            <option value="fire">fire</option>
            <option value="ghost">ghost</option>
            <option value="grass">grass</option>
            <option value="ground">ground</option>
            <option value="ice">ice</option>
            <option value="poison">poison</option>
            <option value="psychic">psychic</option>
            <option value="rock">rock</option>
            <option value="shadow">shadow</option>
            <option value="steel">steel</option>
            <option value="unknow">unknow</option>
            <option value="water">water</option>
          </select>
          {selectValue && <h2 className="mt-3">{selectValue}</h2>}
        </div>
      </div>
        <div className={s.filterBy}>
          <h3>Created in</h3>
          
        </div>    
      </div>
    
  )
}

